import React from "react";
import './toDoList.css';

class Button extends React.Component {
    
    constructor(props) {
        super(props);
        this.state = {
         date : new Date()

        }
    }
    
    sendToConsole = () => {
        console.log('finished Task was changed at ' + this.state.date.toLocaleTimeString()+' '+ this.state.date.toLocaleDateString());
    }
    render() {

        return (
            <button onClick={this.sendToConsole} className="btn">Count finished Tasks</button>
        )
    }

}

export default Button;
